mapper.select:
SELECT

FROM app_recommend a

WHERE a.id = #{id}

SELECT

FROM app_recommend a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.category_id = #{categoryId.id}


        AND a.isindex = #{isindex}


        AND a.name LIKE
        '%'||#{name}||'%'
        '%'+#{name}+'%'
        concat('%',#{name},'%')


        AND a.program_id = #{programId.id}


        AND a.status = #{status}




        ORDER BY ${page.orderBy}


        ORDER BY a.sort DESC

SELECT

FROM app_recommend a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.sort ASC

mapper.update:
UPDATE app_recommend SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT * FROM cms_program a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT * FROM cms_category a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT a.id,a.sort FROM app_recommend a

WHERE a.sort > #{sort}

ORDER BY a.sort ASC limit 0,1

SELECT a.id,a.sort FROM app_recommend a

WHERE a.sort < #{sort}

ORDER BY a.sort DESC limit 0,1
