mapper.select:
SELECT

FROM cms_category a

WHERE a.id = #{id}

SELECT
FROM cms_category a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.parent_id = #{parent.id}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.description LIKE '%'||#{description}||'%' '%'+#{description}+'%' concat('%',#{description},'%')
AND a.sync_state = #{syncState}
AND a.status = #{status}
ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM cms_category a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE cms_category SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
