mapper.select:
SELECT

FROM cms_movie a

WHERE a.id = #{id}

SELECT

FROM cms_movie a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND program.name LIKE
        '%'||#{programId.name}||'%'
        '%'+#{programId.name}+'%'
        concat('%',#{programId.name},'%')


        AND program.id = #{programId.id}


        AND a.name LIKE
        '%'||#{name}||'%'
        '%'+#{name}+'%'
        concat('%',#{name},'%')


        AND a.episode = #{episode}


        AND a.status = #{status}




        ORDER BY ${page.orderBy}


        ORDER BY a.sort DESC

SELECT

FROM cms_movie a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.sort ASC

mapper.update:
UPDATE cms_movie SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.id,a.sort FROM cms_movie a

WHERE a.sort > #{sort}

ORDER BY a.sort ASC limit 0,1

SELECT a.id,a.sort FROM cms_movie a

WHERE a.sort < #{sort}

ORDER BY a.sort DESC limit 0,1
