mapper.select:
SELECT

FROM cms_picture_info a

WHERE a.id = #{id}

SELECT
FROM cms_picture_info a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.program_id LIKE '%'||#{programId.id}||'%' '%'+#{programId.id}+'%' concat('%',#{programId.id},'%')
AND a.movie_id LIKE '%'||#{movieId.id}||'%' '%'+#{movieId.id}+'%' concat('%',#{movieId.id},'%')
AND a.description LIKE '%'||#{description}||'%' '%'+#{description}+'%' concat('%',#{description},'%')
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.sort DESC

SELECT

FROM cms_picture_info a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_picture_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
