mapper.select:
SELECT 

FROM cms_program_cast a

WHERE a.id = #{id}

SELECT 

FROM cms_program_cast a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.program_id = #{programId.id}


		AND a.cast_id = #{castId.id}


		AND a.cast_type = #{castType}


		AND a.status = #{status}


		AND a.sort = #{sort}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cms_program_cast a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_program_cast SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
