mapper.select:
SELECT

FROM cms_program_category a

WHERE a.id = #{id}

SELECT

FROM cms_program_category a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.program_id = #{programId.id}


        AND a.category_id = #{categoryId.id}


        AND a.status = #{status}


        AND a.sort = #{sort}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM cms_program_category a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_program_category SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
