mapper.select:
SELECT

FROM cms_program_lang a

WHERE a.id = #{id}

SELECT
FROM cms_program_lang a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.program_id LIKE '%'||#{programId.id}||'%' '%'+#{programId.id}+'%' concat('%',#{programId.id},'%')
AND a.lang_id LIKE '%'||#{langId.id}||'%' '%'+#{langId.id}+'%' concat('%',#{langId.id},'%')
AND a.status = #{status}
AND a.sort = #{sort}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT

FROM cms_program_lang a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_program_lang SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE cms_program_lang SET
  lang_id = #{langId.id}
WHERE program_id = #{programId.id}
