mapper.select:
SELECT 

FROM cms_program_online a

WHERE a.id = #{id}

SELECT
FROM cms_program_online a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.movie_id LIKE '%'||#{movieId}||'%' '%'+#{movieId}+'%' concat('%',#{movieId},'%')
AND a.program_id LIKE '%'||#{programId}||'%' '%'+#{programId}+'%' concat('%',#{programId},'%')
AND a.dealed = #{dealed}
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM cms_program_online a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_program_online SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
