mapper.select:
SELECT

FROM cms_program_type a

WHERE a.id = #{id}

SELECT

FROM cms_program_type a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.program_id LIKE
        '%'||#{programId.id}||'%'
        '%'+#{programId.id}+'%'
        concat('%',#{programId.id},'%')


        AND a.type_id = #{typeId}


        AND a.status = #{status}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM cms_program_type a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_program_type SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE cms_program_type SET
	type_id = #{typeId.id}
WHERE program_id = #{programId.id}
