mapper.select:
SELECT

FROM cms_recommend a

WHERE a.id = #{id}

SELECT
FROM cms_recommend a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.category_id LIKE '%'||#{categoryId.id}||'%' '%'+#{categoryId.id}+'%' concat('%',#{categoryId.id},'%')
AND a.type_id LIKE '%'||#{typeId.id}||'%' '%'+#{typeId.id}+'%' concat('%',#{typeId.id},'%')
AND a.program_id LIKE '%'||#{programId.id}||'%' '%'+#{programId.id}+'%' concat('%',#{programId.id},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.isindex = #{isindex}
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.sort DESC

SELECT

FROM cms_recommend a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_recommend SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT * FROM cms_program a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT * FROM cms_category a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT * FROM cms_type a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
AND a.category_id = #{category.id}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT a.id,a.sort FROM cms_recommend a

WHERE a.sort > #{sort}

ORDER BY a.sort ASC limit 0,1

SELECT a.id,a.sort FROM cms_recommend a

WHERE a.sort < #{sort}

ORDER BY a.sort DESC limit 0,1
