mapper.select:
SELECT 

FROM cms_region a

WHERE a.id = #{id}

SELECT
FROM cms_region a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.description LIKE '%'||#{description}||'%' '%'+#{description}+'%' concat('%',#{description},'%')
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.sort DESC

SELECT 

FROM cms_region a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.sort ASC

mapper.update:
UPDATE cms_region SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
