mapper.select:
SELECT 

FROM cms_sp a

WHERE a.id = #{id}

SELECT
FROM cms_sp a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.link_man LIKE '%'||#{linkMan}||'%' '%'+#{linkMan}+'%' concat('%',#{linkMan},'%')
AND a.mobile LIKE '%'||#{mobile}||'%' '%'+#{mobile}+'%' concat('%',#{mobile},'%')
AND a.address LIKE '%'||#{address}||'%' '%'+#{address}+'%' concat('%',#{address},'%')
AND a.compact_number = #{compactNumber}
AND a.compact_starttime = #{compactStarttime}
AND a.compact_endtime = #{compactEndtime}
AND a.movie_downed = #{movieDowned}
AND a.supply_stream = #{supplyStream}
AND a.company_name LIKE '%'||#{companyName}||'%' '%'+#{companyName}+'%' concat('%',#{companyName},'%')
AND a.ctype = #{ctype}
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.sort DESC

SELECT 

FROM cms_sp a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.sort ASC

mapper.update:
UPDATE cms_sp SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
