mapper.select:
SELECT 

FROM cms_type a

WHERE a.id = #{id}

SELECT 

FROM cms_type a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.category_id = #{category.id}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.sort DESC

SELECT 

FROM cms_type a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_type SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.id,a.sort FROM cms_type a

WHERE a.sort > #{sort}

ORDER BY a.sort ASC limit 0,1

SELECT a.id,a.sort FROM cms_type a

WHERE a.sort < #{sort}

ORDER BY a.sort DESC limit 0,1
