mapper.select:
SELECT 

FROM user_bind_stbid a

WHERE a.id = #{id}

SELECT 

FROM user_bind_stbid a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.stbid = #{stbid}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM user_bind_stbid a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE user_bind_stbid SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
