SELECT

FROM user_play_history a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.user_id = #{user.id}


        AND a.program_id = #{programId.id}


        AND a.movice_id = #{moviceId.id}


        AND a.movicestream_id = #{movicestreamId.id}


        AND a.status = #{status}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM user_play_history a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

