mapper.select:
SELECT 

FROM user_token a

WHERE a.id = #{id}

SELECT 

FROM user_token a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.token = #{token}


		AND a.user_id = #{user.id}


		AND a.admin_id = #{adminId}


		AND a.expire_time = #{expireTime}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM user_token a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE user_token SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
