mapper.select:
SELECT 

FROM zms_category a

WHERE a.id = #{id}

SELECT
FROM zms_category a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.parent_id = #{parent.id}
AND a.status = #{status}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
ORDER BY a.sort DESC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM zms_category a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE zms_category SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
