mapper.select:
SELECT

FROM zms_channel a

WHERE a.id = #{id}

SELECT

FROM zms_channel a


    a.del_flag = #{DEL_FLAG_NORMAL}


        AND a.name LIKE
        '%'||#{name}||'%'
        '%'+#{name}+'%'
        concat('%',#{name},'%')



        AND a.channel_number = #{channelNumber}



        AND a.type = #{type}


        AND a.subtype = #{subtype}



        AND a.storage_duration = #{storageDuration}



        AND a.language LIKE
        '%'||#{language}||'%'
        '%'+#{language}+'%'
        concat('%',#{language},'%')



        AND a.status = #{status}





        ORDER BY ${page.orderBy}


        ORDER BY a.sort DESC

SELECT

FROM zms_channel a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.sort ASC

mapper.update:
UPDATE zms_channel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
