mapper.select:
SELECT 

FROM zms_channel_recommed a

WHERE a.id = #{id}

SELECT
FROM zms_channel_recommed a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.channel_id LIKE '%'||#{channelId.id}||'%' '%'+#{channelId.id}+'%' concat('%',#{channelId.id},'%')
AND a.category_id LIKE '%'||#{categoryId.id}||'%' '%'+#{categoryId.id}+'%' concat('%',#{categoryId.id},'%')
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.sort DESC

SELECT 

FROM zms_channel_recommed a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE zms_channel_recommed SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT * FROM zms_channel a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT * FROM zms_category a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT a.id,a.sort FROM zms_channel_recommed a

WHERE a.sort > #{sort}

ORDER BY a.sort ASC limit 0,1

SELECT a.id,a.sort FROM zms_channel_recommed a

WHERE a.sort < #{sort}

ORDER BY a.sort DESC limit 0,1
