mapper.select:
SELECT

FROM zms_channel_schedule a

WHERE a.id = #{id}

SELECT
FROM zms_channel_schedule a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.channel_id = #{channelId.id}
AND a.content_id = #{contentId}
AND a.code LIKE '%'||#{code}||'%' '%'+#{code}+'%' concat('%',#{code},'%')
AND a.channel_code LIKE '%'||#{channelCode}||'%' '%'+#{channelCode}+'%' concat('%',#{channelCode},'%')
AND a.program_name LIKE '%'||#{programName}||'%' '%'+#{programName}+'%' concat('%',#{programName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT

FROM zms_channel_schedule a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE zms_channel_schedule SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
