mapper.select:
SELECT

FROM zms_channel_stream a

WHERE a.id = #{id}

SELECT

FROM zms_channel_stream a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.video_type = #{videoType}


        AND a.audio_type = #{audioType}


        AND a.resolution = #{resolution}


        AND a.channel_id = #{channelId.id}




        ORDER BY ${page.orderBy}


        ORDER BY a.sort ASC

SELECT

FROM zms_channel_stream a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.sort ASC

mapper.update:
UPDATE zms_channel_stream SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
