mapper.select:
SELECT

FROM zms_user_channel_favorite a

WHERE a.id = #{id}

SELECT
FROM zms_user_channel_favorite a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.username LIKE '%'||#{username}||'%' '%'+#{username}+'%' concat('%',#{username},'%')
AND a.nickname LIKE '%'||#{nickname}||'%' '%'+#{nickname}+'%' concat('%',#{nickname},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.user_id = #{user.id}
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT

FROM zms_user_channel_favorite a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE zms_user_channel_favorite SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
